import React from 'react'

class FilteredList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            size: "All",
            type: "All"
        };
        this.onSelectFilterSize = this.onSelectFilterSize.bind(this);
        this.onSelectFilterType = this.onSelectFilterType.bind(this);
    }

    onSelectFilterSize = event => {
        this.setState({
            size: event
        })
    };

    onSelectFilterType = event => {
        this.setState({
            type: event
        })
    };

    render() {
        return (
            <DisplayList list={this.props.list.filter(this.matchesFilterSize)}/>
        )
    }


}

export default FilteredList;
